var url = require('url');
//Здесь идентификатор будет ('server:request')
var debug = require('debug')('server:request');

module.exports = function (req, res) {
    var urlParsed = url.parse(req.url, true);

    debug("Got request", req.method, req.url);

    if (req.method == 'GET' && urlParsed.pathname == '/echo' && urlParsed.query.message) {
        var message = urlParsed.query.message;
        debug("Echo: " + message);
        res.end(message);
        return;
    }

    debug("Unknown URL");

    res.statusCode = 404;
    res.end('Page no found')
};
//Выводим в cmd:
//Устанавливаем переменное окружение в cmd set DEBUG=server, затем node server.js,
//на данный момент идёт вывод от сервера,
//чтобы с браузера был вывод в cmd:
//set DEBUG=server:*, затем node server.js
//Теперь от сервера нет вывода, а от обработчика запроса он появился
