//Логирование
var http = require('http');
//Установим модуль debug, при подключении указываем идентификатор,
//которому он будет метить весь вывод из данного файла ('server')
var debug = require('debug')('server');

var server = http.createServer();

server.on('request', require('./request'));

server.listen(1337);
//Вместо console.log пишем debug
debug('Server is running');
